steps.init:
$('#intro').off('click').on('click', function() {
  var intro = introJs();
  intro.setOptions({
    steps: [
      {
        element: $('.board.panel')[0],
        intro: '该区域为教程内容区。'
      },
      {
        element: $('.panel.html')[0],
        intro: '该区域为 HTML 模板区域。'
      },
      {
        element: $('.panel.javascript')[0],
        intro: '该区域为 JS 区。其中变量 <code>html</code> 指代上面 HTML 区内容的DOM 对象'
      },
      {
        element: $('.panel.console')[0],
        intro: '该区域为 JS 控制台区。可以在这里编写代码与 HTML 区和 Javascript 区中的内容交互。',
        position: 'top'
      },
      {
        element: $('.exe.btn')[0],
        intro: '点击该按钮将运行 HTML 区和 Javascript 区的代码。',
        position: 'left'
      },
      {
        element: $('.exe.btn')[1],
        intro: '电击该按钮将运行 JS 控制台区的代码。',
        position: 'left'
      },
      {
        element: $('.panel.output')[0],
        intro: '这里将展示 HTML 区和 JavaScript 区的运行结果。',
        position: 'top'
      },
      {
        element: $('#reset')[0],
        intro: '点击该按钮将恢复右边区域中的教程代码！'
      }
    ]
  });
  intro.start();
})

steps.javascript:
